import React from "react"
import User from "./User"

class Users extends React.Component {
  constructor(props) {
      super(props)
      this.state = {
        users: [
          {
            id: 1,
            firstname: 'Anton',
            lastname: 'Petukh',
            bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Hic, numquam?',
            age: 30,
            isHappy: true
          },
          {
            id: 2,
            firstname: 'Andrey',
            lastname: 'Alc',
            bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Hic, numquam?',
            age: 26,
            isHappy: false
          }
        ]
      }
  }
  render() {
    if(this.state.users.length > 0)
    return (
        <div>
          {this.state.users.map((el) => (<div className="user" key={el.id}>
            <h3>{el.firstname} {el.lastname}</h3>
            <p>{el.bio}</p>
            <b>{el.isHappy ? 'Счастлив' : 'Не особо :('}</b>
          </div>))}
        </div>
    )
    else
    return (
      <div className="user">
        <h3>Пользователей нет</h3>
      </div>
    )
  }

}

export default Users
