import React from "react"
import User from "./User"

class Users extends React.Component {
  constructor(props) {
      super(props)
      this.state = {
        users: [
          {
            id: 1,
            firstname: 'Anton',
            lastname: 'Petukh',
            bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Hic, numquam?',
            age: 30,
            isHappy: true
          },
          {
            id: 2,
            firstname: 'Andrey',
            lastname: 'Alc',
            bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Hic, numquam?',
            age: 26,
            isHappy: false
          }
        ]
      }
  }
  render() {
    if(this.props.users.length > 0)
    return (
        <div>
          {this.props.users.map((el) => (<User key={el.id} user={el}/>))}
        </div>
    )
    else
    return (
      <div className="user">
        <h3>Пользователей нет</h3>
      </div>
    )
  }

}

export default Users
